using UnityEngine; using GooglePlayGames; using GooglePlayGames.BasicApi; using UnityEngine.SocialPlatforms; using System.Collections;   public class GPGS : MonoBehaviour {  	public static bool Activate()     {         PlayGamesPlatform.Activate();         return LogIn();     }      public static bool LogIn()     {         bool isSuccess = false;         Social.localUser.Authenticate((bool success) =>         {             isSuccess = success;             if (success)             {                 Debug.Log("Giriş Yapıldı");             }             else             {                 Debug.Log("Giriş Yapılamadı");             }         });         return isSuccess;     }       public static void UnlockAchivements(string achivementId)     {         Social.ReportProgress(achivementId, 100.0f, (bool success) =>       {           if (success)           {               Debug.Log("Başarı kilidi açıldı");           }       });     }      public static void PostScore(string scoreId , long score)     {         Social.ReportScore(score, scoreId, (bool success) =>       {           if (success)           {               Debug.Log("Skor Gönderildi");           }           else           {               Debug.Log("Skor Gönderilemedi.");           }       });     }          public static void ShowAchivementUI()     {         Social.ShowAchievementsUI();     }      public static void ShowScoreboardUI()     {         Social.ShowLeaderboardUI();     }  } 